mapper.insert:
insert into product (
    name, category_id, code, price, cost,
    stock, status, image, description,
    create_time, update_time, create_user, update_user
) values (
             #{name}, #{categoryId}, #{code}, #{price}, #{cost},
             #{stock}, #{status}, #{image}, #{description},
             #{createTime}, #{updateTime}, #{createUser}, #{updateUser}
         )

mapper.update:
update product
name = #{name}, category_id = #{categoryId}, code = #{code}, price = #{price}, cost = #{cost}, stock = #{stock}, status = #{status}, image = #{image}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select * from product

    and name like concat('%', #{name}, '%')
    and code like concat('%', #{code}, '%')
    and category_id = #{categoryId}
    and status = #{status}

order by create_time desc
