mapper.insert:
insert into sales_order (
    order_number, customer_name, total_amount, status,
    create_time, completion_time, operator_id
) values (
             #{orderNumber}, #{customerName}, #{totalAmount}, #{status},
             #{createTime}, #{completionTime}, #{operatorId}
         )

mapper.update:
update sales_order
order_number = #{orderNumber}, customer_name = #{customerName}, total_amount = #{totalAmount}, status = #{status}, completion_time = #{completionTime}, operator_id = #{operatorId},
where id = #{id}

mapper.select:
select so.id, so.order_number, so.customer_name, so.total_amount, so.status, so.create_time, so.completion_time, u.name as operator_name from sales_order so left join employee u on so.operator_id = u.id
and so.order_number like concat('%', #{orderNumber}, '%')
and so.customer_name like concat('%', #{customerName}, '%')
and so.status = #{status}
and so.create_time >= #{beginTime}
and so.create_time <= #{endTime}
order by so.create_time desc

select count(*)
from sales_order so


        and so.order_number like concat('%', #{orderNumber}, '%')


        and so.customer_name like concat('%', #{customerName}, '%')


        and so.status = #{status}


        and so.create_time >= #{beginTime}


        and so.create_time <= #{endTime}
